    props?: Attributes & P | null,
    ...children: ReactNode[]): ReactElement<P>;
//源码实现
export function createElement(type, config, children) {
  let propName;
  // Reserved names are extracted
  const props = {};

  let key = null;
  let ref = null;
  let self = null;
  let source = null;

  if (config != null) {
    if (hasValidRef(config)) {
